import { useState } from "react";
import { Header } from "./components/Header";
import { CountSection } from "./components/CountSection";
import "/src/css/app.css";
const nick1 =
  "data:image/jpeg;base64,";

const nick2 =
  "data:image/jpeg;base64,";

const nick3 =
  "data:image/jpeg;base64,";

const nick4 =
  "data:image/jpeg;base64,";

const nicks = [nick1, nick2, nick3, nick4];

function App() {
  const [slideIndex, setSlideIndex] = useState(0);
  const currentImage = nicks[slideIndex];
  const isFirst = slideIndex === 0;
  const isLast = slideIndex === nicks.length - 1;
  return (
    <>
      <Header></Header>
      <CountSection></CountSection>
      <div className="carousel">
        <div className="image-container">
          <img src={currentImage} alt="nick1" />
          <p>some text</p>
          <h3>This is some description about this image</h3>
        </div>
        <div className="controls">
          <button
            onClick={() => {
              if (slideIndex > 0) setSlideIndex(slideIndex - 1);
              else setSlideIndex(nicks.length - 1);
            }}
            disabled={isFirst}
          >
            Previous
          </button>
          <button
            onClick={() => {
              if (slideIndex < nicks.length - 1) setSlideIndex(slideIndex + 1);
              else setSlideIndex(0);
            }}
            disabled={isLast}
          >
            Next
          </button>
        </div>
      </div>
    </>
  );
}

export default App;
